let grid = createGrid(rows, cols);
let currentStateIndex = 0;
let generationCount = 0;


function createGrid(rows, cols) {
    return new Array(rows).fill(null).map(() => new Array(cols).fill(0));
}

function initializeGrid() {
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            grid[row][col] = initialState[row] && initialState[row][col] ? 1 : 0;
        }
    }
}


function drawGrid() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            const cell = grid[row][col];
            ctx.beginPath();
            ctx.rect(col * cellSize, row * cellSize, cellSize, cellSize);
            ctx.fillStyle = cell ? 'black' : 'white';
            ctx.fill();
        }
    }
}

function updateGrid() {
    let newGrid = createGrid(rows, cols);
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            const cell = grid[row][col];
            let liveNeighbors = 0;
            for (let i = -1; i <= 1; i++) {
                for (let j = -1; j <= 1; j++) {
                    if (i === 0 && j === 0) continue;
                    const x = row + i;
                    const y = col + j;
                    if (x >= 0 && x < rows && y >= 0 && y < cols) {
                        liveNeighbors += grid[x][y];
                    }
                }
            }
            if (cell === 1 && liveNeighbors < 2 || liveNeighbors > 3) {
                newGrid[row][col] = 0;
            } else if (cell === 0 && liveNeighbors === 3) {
                newGrid[row][col] = 1;
            } else {
                newGrid[row][col] = cell;
            }
        }
    }
    grid = newGrid;
}

function loadState(index) {
    const state = states[index];
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            grid[row][col] = state[row] && state[row][col] ? 1 : 0;
        }
    }
}


function gameLoop() {
    drawGrid();
    updateGrid();
    generationCount++;

    if (generationCount >= generationsPerState) {
        currentStateIndex++;
        if (currentStateIndex < states.length) {
            loadState(currentStateIndex);
            generationCount = 0;
        } else {
            // Stop the game loop or loop back to the first state
            currentStateIndex = 0;
            loadState(currentStateIndex);
        }
    }

    if (generationCount === 1) {
        var speed = 1000;
    } else {
        var speed = 200;
    }
    setTimeout(gameLoop, speed); // speed of the game
}


// execute
loadState(currentStateIndex);
gameLoop();

